import iconAuth from '../images/Union.png';

const InfoTooltip = ({ name, isOpen, title, onClose, icon }) => {
  return (
    <div className={`popup popup_type_${name} ${isOpen ? 'popup_opened' : ''}`}>
      <div className="popup__window">
        <button
          className={`popup__close-button popup__close-button_${name}`}
          type="button"
          aria-label="close"
          onClick={onClose}
        ></button>
        <img
          className="popup__icon"
          alt="Результат авторизации"
          src={iconAuth}
        />
        <h2 className="popup__title popup__title_tooltip">{title}</h2>
      </div>
    </div>
  );
};

export default InfoTooltip;
