import { useState } from 'react';
import Loader from './Loader';
import { Link } from 'react-router-dom';

const Register = ({
  title,
  onSubmit,
  loadingText,
  isLoading,
  buttonText,
  description,
}) => {
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');

  const handleSubmit = (evt) => {
    evt.preventDefault();
  };

  return (
    <div className="registration">
      <h2 className="registration__title">{title}</h2>
      <form className={`registration__form`} noValidate onSubmit={onSubmit}>
        <input
          className="popup__input popup__input_el_first-name"
          type="text"
          name="name"
          placeholder="Имя"
          minLength="2"
          maxLength="40"
          required
        />
        <span className="popup__input-error firstname-input-error"></span>
        <input
          className="popup__input popup__input_el_about"
          value={description || ''}
          type="text"
          name="about"
          placeholder="О себе"
          minLength="2"
          maxLength="200"
          required
        />
        <span className="popup__input-error about-input-error"></span>
        {isLoading ? (
          <Loader loadingText={loadingText} />
        ) : (
          <button className="popup__save-button" type="submit">
            {buttonText}
          </button>
        )}
      </form>
    </div>
  );
};

export default Register;
