import React from 'react';

function Main() {

    const [openEditAvatar, setOpenEditAvatar] = React.useState(false);
    const [openEditProfile, setOpenEditProfile] = React.useState(false);
    const [openAddCard, setOpenAddCard] = React.useState(false);


    React.useEffect(() => {
        function handleEditAvatarClick() {
            setOpenEditAvatar(true);
        }

        document.addEventListener('click', handleEditAvatarClick);
        document.querySelector('.popup_edit_avatar').classList.add('.popup_opened');

        return () => {
            document.querySelector('.popup_edit_avatar').classList.remove('.popup_opened');
            document.removeEventListener('click', handleEditAvatarClick);
        };
    });

    React.useEffect(() => {
        function handleEditProfileClick() {
            setOpenEditProfile(true);
        }

        document.addEventListener('click', handleEditProfileClick);
        document.querySelector('.popup_edit_profile').classList.add('.popup_opened');

        return () => {
            document.querySelector('.popup_edit_profile').classList.remove('.popup_opened');
            document.removeEventListener('click', handleEditProfileClick);
        };
    });

    React.useEffect(() => {
        function handleAddPlaceClick() {
            setOpenAddCard(true);
        }

        document.addEventListener('click', handleAddPlaceClick);
        document.querySelector('.popup_add_card').classList.add('.popup_opened');

        return () => {
            document.querySelector('.popup_add_card').classList.remove('.popup_opened');
            document.removeEventListener('click', handleAddPlaceClick);
        };
    });

    return (
        <main className="content">

        <section className="profile">
            <div className="profile__avatar-overlay">
                <img src="#" alt="Аватар" className="profile__avatar" />
            </div>
            <div className="profile__info">
                <h1 className="profile__title">Жак-Ив Кусто</h1>
                <p className="profile__text">Иследователь океанов</p>
                <button className="profile__edit-button, {handleAddPlaceClick}" type="button"></button>
            </div>
            <button aria-label="add" className="profile__add-button" type="button"></button>
        </section>

        <section className="elements"></section>

        </main>
    );
}

export default Main;
