import PopupWithForm from './PopupWithForm';

function DeleteCardPopup({ isOpen, onClose, card, onDeleteCard, isLoad }) {
  function handleSubmit(e) {
    e.preventDefault();

    onDeleteCard(card);
  }

  function handleLoad(isLoad) {
    if (isLoad) {
      <PopupWithForm buttonText="Удаление..." />;
    } else {
      <PopupWithForm buttonText="Да" />;
    }
  }

  return (
    <PopupWithForm
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      isLoad={isLoad}
      name="delete-card"
      title="Вы уверены?"
      buttonText="Да"
    />
  );
}

export default DeleteCardPopup;
