import React from 'react';
import api from '../utils/api';
import Card from './Card';
import { CurrentUserContext } from '../contexts/CurrentUserContext';


function Main(props) {

    const [cards, setCards] = React.useState([]);

    const currentUser = React.useContext(CurrentUserContext);
        console.log(currentUser);

    React.useEffect(() => {
        api.getInitialCards()
        .then((cardList) => {
            setCards(cardList);
        })
        .catch((err) => {
            console.log(err);
        })
    }, []);

    function handleCardLike(card) {
        // Снова проверяем, есть ли уже лайк на этой карточке
        const isLiked = card.likes.some(i => i._id === currentUser._id);

        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.changeLikeCardStatus(card._id, !isLiked).then((newCard) => {
            setCards((state) => state.map((c) => c._id === card._id ? newCard : c));
        });
    }

    function handleCardDelete(card) {
        // проверяем, нами ли создана карточка
        const isOwn = props.card.owner._id === currentUser._id;
        console.log(isOwn);
        // Отправляем запрос в API и получаем обновлённые данные карточки
        api.deleteCard(card._id, !isOwn).then(() => {

        });
    }

    handleCardDelete()
    return (
        <main className="content">

        <section className="profile">
            <div className="profile__avatar-overlay">
                <img src={currentUser.avatar} style={{ backgroundImage: `url(${currentUser.avatar})` }} alt="Аватар" className="profile__avatar" onClick={props.onEditAvatar} />
            </div>
            <div className="profile__info">
                <h1 className="profile__title">{currentUser.name}</h1>
                <p className="profile__text">{currentUser.about}</p>
                <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
            </div>
            <button aria-label="add" className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
        </section>

        <section className="elements">
            {cards.map((item) => (
            <Card 
            card={item} 
            key={item._id}
            onCardClick={props.onCardClick}
            onCardLike={handleCardLike}
            onCardDelete={handleCardDelete} />))}
        </section>

        </main>
    );
}

export default Main;
