import React from 'react';
import api from '../utils/api';

function Main(props) {

const [userName, setUserName] = React.useState('');
const [userDescription, setUserDescription] = React.useState('');
const [userAvatar, setUserAvatar] = React.useState('');

React.useEffect(() => {
    api.getUserInfo()
    .then((res) => {
        console.log(rea),
    setUserName(res[0].name),
    setUserDescription(res[0].about),
    setUserAvatar(res[0].avatar)
  })
  .catch((err) => {
      console.log(err);
})
}, [])

    return (
        <main className="content">

        <section className="profile">
            <div className="profile__avatar-overlay">
                <img src={userAvatar} alt="Аватар" className="profile__avatar" onClick={props.onEditAvatar} />
            </div>
            <div className="profile__info">
                <h1 className="profile__title">{userName}</h1>
                <p className="profile__text">{userDescription}</p>
                <button className="profile__edit-button" type="button" onClick={props.onEditProfile}></button>
            </div>
            <button aria-label="add" className="profile__add-button" type="button" onClick={props.onAddPlace}></button>
        </section>

        <section className="elements"></section>

        </main>
    );
}

export default Main;

