import PopupWithForm from './PopupWithForm';

function DeleteCardPopup({ isOpen, onClose, card, onDeleteCard, onLoad }) {
  function handleSubmit(e) {
    e.preventDefault();

    onDeleteCard(card);
  }

  function handleLoad(isLoad) {
    if (isLoad) {
      buttonText = 'Удаление...';
    } else {
      buttonText = 'Да';
    }
  }

  return (
    <PopupWithForm
      isOpen={isOpen}
      onClose={onClose}
      onSubmit={handleSubmit}
      onLoad={handleLoad}
      name="delete-card"
      title="Вы уверены?"
      buttonText="Да"
    />
  );
}

export default DeleteCardPopup;
