import React from "react";

function PopupWithForm(popups) {
    return (
        <div className={`popup popup_type_${popups.name} ${popups.isOpen ? 'popup_opened' : ''}`}>
        <div className="popup__window">
            <h2 className="popup__title">{popups.title}</h2> 
            <button className={`popup__close-button popup__close-button_${popups.name}`} type="button" aria-label="close"></button>
            <form className={`popup__form popup__form_type_${popups.name}`} name="popupForm" novalidate>
                <input className="popup__input popup__input_el_first-name" type="text" name="firstname" placeholder="Имя" minlength="2" maxlength="40" required />
                <span className="popup__input-error firstname-input-error"></span>
                <input className="popup__input popup__input_el_about" type="text" name="about" placeholder="О себе" minlength="2" maxlength="200" required />
                <span className="popup__input-error about-input-error"></span>
                <button className="popup__save-button" type="submit">Сохранить</button>
            </form>
        </div>
    </div>
    )
}

export default PopupWithForm;
